tinymce.PluginManager.add('blockquote', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('blockquote', {
        text: 'blockquote',
        icon: false,
        onclick: function() {
            editor.insertContent('<blockquote>' + editor.selection.getContent() + '</blockquote>');
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('blockquote', {
        text: 'blockquote plugin',
        context: 'tools',
    });
});