tinymce.PluginManager.add('kavich', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('kavich', {
        text: '«»',
        icon: false,
        onclick: function() {
            editor.insertContent('<q>' + editor.selection.getContent() + '</q>');
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('kavich', {
        text: 'kavich plugin',
        context: 'tools',
    });
});