tinymce.PluginManager.add('noindex', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('noindex', {
        text: 'noindex',
        icon: false,
        onclick: function() {
            editor.insertContent('<noindex>' + editor.selection.getContent() + '</noindex>');
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('noindex', {
        text: 'noindex plugin',
        context: 'tools',
    });
});